mapper.select:
SELECT COUNT(*) > 0
FROM users
WHERE email = #{email}
  AND id != #{id}

SELECT COUNT(*) > 0
FROM users
WHERE nickname = #{nickname}
  AND id != #{id}

mapper.update:
UPDATE users
SET nickname = #{nickname},
email = #{email}

    , profileUrl = #{profileUrl}


    , password = #{password}

WHERE id = #{id}

UPDATE users
SET password = #{password}
WHERE id = #{id}

UPDATE users
SET is_deleted = 1
WHERE id = #{id}
